mapper.update:
update dish
name = #{name}, price = #{price}, category_id = #{categoryId}, image = #{image}, description = #{description}, status = #{status}, update_user = #{updateUser}, create_user = #{createUser}, update_time = #{updateTime}, create_time = #{createTime},
where id = #{id}

mapper.select:
select d.*,c.name as categoryName from dish d left join category c on d.category_id = c.id


and d.name like concat ('%',#{name},'%')


    d.category_id = #{categoryId}


    d.status = #{status}


order by d.create_time desc

select * from dish

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc
